mapper.insert:
INSERT INTO common.access_log (
  uri, method, response_code, processing_time, "timestamp", remote_address, remote_host,
  referer, session_id, user_id )
VALUES (
  #{uri}, #{method}, #{response_code}, #{processing_time}, #{timestamp}, #{remote_address}, #{remote_host},
  #{referer}, #{session_id}, #{user_id}
);
